实现：利用了async、栈、队列相关知识点
let timeout = (time) => {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            resolve(true)
        }, time*1000)
    })
}

function machine(name) {
    let tasks = []

    function doTask(act) {
        let task = () => {
            console.log(`${name} ${act}`);
        }
        tasks.push(task)
        return this;
    }

    function execute(){
        tasks.reduce(async (promise, task) => {
            await promise;
            await task();
        }, Promise.resolve);
    }

    function waitFirst(sec) {
        const task = async () => {
            console.log(`wait ${sec}s`);
            await timeout(sec);
        };

        tasks.unshift(task);
        return this;
    }
    function wait(sec){
        const task = async () => {
            console.log(`wait ${sec}s`);
            await timeout(sec);
        };
        tasks.push(task);
        return this
    }

    return {
        execute,
        do: doTask,
        wait: wait,
        waitFirst
    };
}

