function parseRule(rule) {
    return rule.split("->");
}

function findRightLetter(rules) {
    let right = rules[0][1];

    let leftRules = []
    rules.forEach(item => {
        if (right == item[0]) {
            right = item[1];
        }else {
            leftRules.push(item)
        }
    });

    return { right, leftRules };
}

function sortByRule(arr, rules) {
    rules = rules.map(item => {
        return parseRule(item);
