function parseType(str) {
    let boolRe = /^(true|false)$/i,
        dateRe = /^\d{4}(0[1-9]|1[0-2])\d{2}$/, // 闰年、月份、日期都需要考虑，这里还是有点繁琐的，没写全
        numRe = /^-?\d+(\.\d+)?(e\d+)?$/ // 负数、小数、科学记数法

    if(boolRe.test(str)){
        return 'Boolean'
    }else if(dateRe.test(str)){
        return 'Date'
    }else if(numRe.test(str)){
        return 'Number'
    }else {
        return 'String'
    }
}

